import React, { useState, useEffect } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function StudentEdit() {
  const { id } = useParams(); // Fetching the id parameter from the URL
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch the student data from API based on the id parameter
    axios.get(`http://127.0.0.1:8000/api/student/edit/${id}`)
      .then(res => {
        const { name, email, phone } = res.data.student;
        setName(name);
        setEmail(email);
        setPhone(phone);
      })
      .catch(err => {
        console.error(err);
        setError('Failed to fetch student data. Please try again later.');
      });
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`http://127.0.0.1:8000/api/student/edit/${id}`, {
        name,
        email,
        phone
      });
      console.log(response.data);
      navigate('/student'); // Redirect to the student list page or another appropriate page
    } catch (err) {
      console.error(err);
      setError('Failed to update student. Please try again later.');
    }
  };

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6 offset-md-3">
          <div className="card">
            <div className="card-header">
              <h4>Edit Student</h4>
              <Link to="/student" className="btn btn-sm btn-danger float-end">Cancel</Link>
            </div>
            <div className="card-body">
              {error && <div className="alert alert-danger">{error}</div>}
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label className="form-label">Name</label>
                  <input
                    type="text"
                    className="form-control"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Email</label>
                  <input
                    type="email"
                    className="form-control"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Phone</label>
                  <input
                    type="text"
                    className="form-control"
                    value={phone}
                    onChange={(e) => setPhone(e.target.value)}
                    required
                  />
                </div>
                <button type="submit" className="btn btn-primary">Update Student</button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default StudentEdit;
