import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function Student() {
  const [students, setStudents] = useState([]);

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/student')
      .then(res => {
        if (Array.isArray(res.data)) {
          setStudents(res.data);
        } else {
          console.error("API response is not an array:", res.data);
          setStudents([]);
        }
      })
      .catch(err => {
        console.error("Error fetching students:", err);
        setStudents([]);
      });

    // Cleanup function if needed
    return () => {
      // Any cleanup code goes here
    };
  }, []); // Empty dependency array to run only once on mount

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-12">
          <div className="card">
            <div className="card-header">
              <h4>Student List</h4>
              <Link to="/" className="btn btn-primary float-end">Add Student</Link>
            </div>
            <div className="card-body">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Course</th>
                    <th>Email</th>
                    <th>Phone</th>
                    <th>Edit</th>
                    <th>Delete</th>
                  </tr>
                </thead>
                <tbody>
                  {students.length > 0 ? (
                    students.map(student => (
                      <tr key={student.id}>
                        <td>{student.id}</td>
                        <td>{student.name}</td>
                        <td>{student.course}</td>
                        <td>{student.email}</td>
                        <td>{student.phone}</td>
                        <td>
                          <Link to={`/edit-student/${student.id}`} className="btn btn-sm btn-warning">Edit</Link>
                        </td>
                        <td>
                          <button className="btn btn-sm btn-danger">Delete</button>
                        </td>
                      </tr>
                    ))
                  ) : (
                    <tr>
                      <td colSpan="7" className="text-center">No students found</td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Student;
