import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [items, setItems] = useState([]);
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [editing, setEditing] = useState(false);
  const [currentItem, setCurrentItem] = useState(null);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    const res = await axios.get('http://localhost:3001/items');
    setItems(res.data.items);
  };

  const addItem = async () => {
    const res = await axios.post('http://localhost:3001/items', { name, description });
    setItems([...items, { id: res.data.id, name, description }]);
    setName('');
    setDescription('');
  };

  const updateItem = async () => {
    const res = await axios.put(`http://localhost:3001/items/${currentItem.id}`, { name, description });
    setItems(items.map(item => (item.id === currentItem.id ? { ...item, name, description } : item)));
    setName('');
    setDescription('');
    setEditing(false);
    setCurrentItem(null);
  };

  const deleteItem = async (id) => {
    await axios.delete(`http://localhost:3001/items/${id}`);
    setItems(items.filter(item => item.id !== id));
  };

  const editItem = (item) => {
    setEditing(true);
    setCurrentItem(item);
    setName(item.name);
    setDescription(item.description);
  };

  return (
    <div className="App">
      <h1>CRUD App with React and SQLite</h1>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          editing ? updateItem() : addItem();
        }}
      >
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Name"
          required
        />
        <input
          type="text"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Description"
          required
        />
        <button type="submit">{editing ? 'Update' : 'Add'} Item</button>
      </form>
      <ul>
        {items.map((item) => (
          <li key={item.id}>
            {item.name} - {item.description}
            <button onClick={() => editItem(item)}>Edit</button>
            <button onClick={() => deleteItem(item.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
