import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Loading from '../components/Loading';

function Student() {
  const [loading, setLoading] = useState(true);
  const [students, setStudents] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/student')
      .then(res => {
        console.log(res);
        if (res.data && Array.isArray(res.data.student)) {
          setStudents(res.data.student);
          setLoading(false);
        } else {
          setError('Invalid data format received from API.');
        }
      })
      .catch(err => {
        console.error(err);
        setError('Failed to fetch student data. Please try again later.');
      });
  }, []);

  if (loading) {
    return (
      <div>
        <Loading />
      </div>
    );
  }

  const studentDetails = students.map((item, index) => (
    <tr key={index}>
      <td>{item.id}</td>
      <td>{item.name}</td>
      <td>{item.email}</td>
      <td>{item.phone}</td>
      <td>
        <Link to={`/student/${item.id}/edit`} className="btn btn-sm btn-success">Edit</Link>
      </td>
      <td>
        <button className="btn btn-sm btn-danger">Delete</button>
      </td>
    </tr>
  ));

  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-12">
          <div className="card">
            <div className="card-header">
              <h4>Student List</h4>
              <Link to="/student/create" className="btn btn-sm btn-primary float-end">Add Student</Link>
            </div>
            <div className="card-body">
              {error && <div className="alert alert-danger">{error}</div>}
              <table className="table table-striped table hover">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Phone</th>
                    <th>Edit</th>
                    <th>Delete</th>
                  </tr>
                </thead>
                <tbody>
                  {studentDetails}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Student;
